    int test_str_size;
    byte buf[255];
#ifdef WOLFSSL_KYBER_ORIGINAL
    int group = WOLFSSL_KYBER_LEVEL5;
#else
    int group = WOLFSSL_ML_KEM_1024;
#endif

    XMEMSET(&test_ctx, 0, sizeof(test_ctx));
