    s = (sp_int_digit)(a[15]); t = (sp_int_digit)(a[14]);
    r[15] = (sp_digit)(((s << n) | (t >> (26U - n))) & 0x3ffffff);
    s = (sp_int_digit)(a[14]); t = (sp_int_digit)(a[13]);
