    s = (sp_int_digit)(a[12]); t = (sp_int_digit)(a[11]);
    r[12] = (sp_digit)(((s << n) | (t >> (26U - n))) & 0x3ffffff);
    s = (sp_int_digit)(a[11]); t = (sp_int_digit)(a[10]);
    r[11] = (sp_digit)(((s << n) | (t >> (26U - n))) & 0x3ffffff);
    s = (sp_int_digit)(a[10]); t = (sp_int_digit)(a[9]);
