        ERROR_OUT(WC_TEST_RET_ENC_EC(ret), out);

    if (XMEMCMP(ss_dec, kyber768_ss, sizeof(kyber768_ss)) != 0)
        ERROR_OUT(WC_TEST_RET_ENC_NC, out);
#endif
