        XMEMSET(tag, 0, sizeof(tag));
        tagSz = sizeof(tag);
        ret = wc_InitCmac_ex(cmac, tc->k, tc->kSz, tc->type, NULL, HEAP_HINT, devId);
        if (ret != 0) {
            ERROR_OUT(WC_TEST_RET_ENC_EC(ret), out);
        }
        ret = wc_AesCmacGenerate_ex(cmac, tag, &tagSz, tc->m, tc->mSz,
                NULL, 0, HEAP_HINT, devId);
        if (ret != 0) {
            ERROR_OUT(WC_TEST_RET_ENC_EC(ret), out);
        }
#endif

